(window.webpackJsonp=window.webpackJsonp||[]).push([[188],{626:function(n,i,t){"use strict";t.r(i);var a=t(7),e=Object(a.a)({},(function(){var n=this,i=n.$createElement,t=n._self._c||i;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"工业互联网-80-个名词术语详细解释"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#工业互联网-80-个名词术语详细解释"}},[n._v("#")]),n._v(" 工业互联网 80 个名词术语详细解释")]),n._v(" "),t("h3",{attrs:{id:"前言"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#前言"}},[n._v("#")]),n._v(" 前言")]),n._v(" "),t("blockquote",[t("blockquote",[t("p",[n._v("随着工业互联网的发展和新理念、新技术的引入，围绕工业互联网出现了大量术语和定义。本篇文章主要对工业互联网术语和定义进行了汇总和梳理，可用于统一业界对关键术语和定义的认识和理解，规范术语和定义的使用")])])]),n._v(" "),t("ol",[t("li",[t("p",[n._v("IT 网络(Information Technology Network)\nIT 网络是用于连接信息系统与终端的数据通信网络。")])]),n._v(" "),t("li",[t("p",[n._v("OT 网络 (Operation Technology Network)\nOT 网络用于连接生产现场设备与系统，实现自动控制的工业通讯网络。")])]),n._v(" "),t("li",[t("p",[n._v("安全策略（Security Policy）\n安全策略是关于如何在组织及其系统内管理、保护和分发资产(包括敏感信息)的\n规则、指令和实践，特别是影响到系统和相关要素的规则、指令和实践。")])]),n._v(" "),t("li",[t("p",[n._v("安全措施（Security Measure）\n安全措施是为了保护资产、抵御威胁、减少脆弱性、降低安全事件的影响，以及\n打击信息犯罪而实施的各种实践、规程和机制。")])]),n._v(" "),t("li",[t("p",[n._v("安全服务（Security Service）\n安全服务是根据安全策略为用户提供的某种安全功能及相关的保障。")])]),n._v(" "),t("li",[t("p",[n._v("安全可控（Securable Control）\n安全可控是指从组织、用户和国家三个角度确保工业互联网业务运行安全、用户\n能选择和控制工业互联网业务，并控制自己的信息，监控部门能控制工业互联网\n业务带来的国家安全风险。")])]),n._v(" "),t("li",[t("p",[n._v("安全事件态势（Security Incident Situation）\n安全事件态势是通过解析还原工业设备所使用的各类通信协议网络数据流，检测\n分析工业协议数据恶意攻击行为，从这些行为中分析出安全事件，并对安全事件\n进行多维度（发生时间、地域、威胁类型、关联资产等）分类处理和趋势展示。")])]),n._v(" "),t("li",[t("p",[n._v("边缘计算（Edge Computing）\n边缘计算是指在靠近物或数据源头的一侧，采用网络、计算、存储、应用核心能\n力为一体的开放平台，就近提供最近端服务。其应用程序在边缘侧发起，产生更快的网络服务响应，满足行业在实时业务、应用智能、安全与隐私保护等方面的基本需求")])]),n._v(" "),t("li",[t("p",[n._v("标识编码（Identifier）\n标识编码指能够唯一识别机器、产品等物理资源和算法、工序等虚拟资源的身份\n符号。")])]),n._v(" "),t("li",[t("p",[n._v("标准必要专利（Standard-Essential Patent）\n标准必要专利指包含在国际标准、国家标准和行业标准中，且在实施标准时必须\n使用的专利，也就是说当标准化组织在制定某些标准时，部分或全部标准草案由\n于技术上或者商业上没有其他可替代方案，无可避免要涉及到专利或专利申请。")])]),n._v(" "),t("li",[t("p",[n._v("产品全生命周期管理(Product Lifecycle Management, PLM)\n产品全生命周期管理是指管理产品从需求、规划、设计、生产、经销、运行、使\n用、维修保养、直到回收再用处置的全生命周期中的信息与过程。")])]),n._v(" "),t("li",[t("p",[n._v("产品数据管理（Product Data Management, PDM）\n产品数据管理是服务于产品研发参与企业的集成协同设计管理系统，为企业提供\n产品研发项目管控、产品数据全生命周期管理、企业设计和工程资源体系管理等\n应用，助力企业实现以产品为中心的基于图文档、设计模型、BOM 和技术状态管\n理的全面设计协同管理。通过与企业 ERP 和 MES 系统的集成，实现产品研发设计\n价值链与上下游参与企业的全面协同。")])]),n._v(" "),t("li",[t("p",[n._v("大数据安全保障（Big Data Security Assurance）\n大数据安全保障对大数据基础设施、数据资产和应用服务进行安全保障的一系列\n适当行为或过程。")])]),n._v(" "),t("li",[t("p",[n._v("大数据服务（Big Data Service）\n大数据服务是指通过底层可伸缩的大数据平台和上层各种大数据应用，支撑机构\n或个人对海量、异构、快速变化数据采集、传输、存储、处理（包括计算、分析、\n可视化等）、交换、销毁等覆盖数据生命周期相关活动的各种数据服务。")])]),n._v(" "),t("li",[t("p",[n._v("分布式控制系统（Distributed Control System, DCS）\n分布式控制系统以计算机为基础，在系统内部（单位内部）对生产过程进行分布\n控制、集中管理的系统。")])]),n._v(" "),t("li",[t("p",[n._v("工业大数据（Industrial Big Data）\n工业大数据即工业数据的总和，即企业信息化数据、工业物联网数据，以及外部\n跨界数据。其中，企业信息化和工业物联网中机器产生的海量时序数据是工业数\n据规模变大的主要来源。")])]),n._v(" "),t("li",[t("p",[n._v("工业大数据平台（Industrial Big Data Platform）\n工业大数据平台是采用分布式存储和计算技术，提供工业大数据的访问和处理，\n提供异构工业数据的一体化管理能力，支持工业大数据应用安全高效运行的软硬\n件集合。")])]),n._v(" "),t("li",[t("p",[n._v("工业防火墙(Industrial Firewall)\n工业防火墙是一个有软件和硬件设备组合而成、在内部网和外部网之间、专用网\n与公共网之间的边界上构造的保护屏障。")])]),n._v(" "),t("li",[t("p",[n._v("工业互联网（Industrial Internet）\n工业互联网是满足工业智能化发展需求，具有低时延、高可靠、广覆盖特点的关\n键网络基础设施，是新一代信息通信技术与先进制造业深度融合所形成的新兴业\n态与应用模式。")])]),n._v(" "),t("li",[t("p",[n._v("工业通信协议（Industrial Communication Protocol）\n工业通信协议是指工业控制领域的双方实体完成通信或服务所必须遵循的规则\n和约定。通过通信信道和设备互连起来的多个不同地理位置的数据通信系统，要\n使其能协同工作实现信息交换和资源共享，它们之间必须具有共同的语言。常见\n的工业通信协议包括 ModBus、OPC/OPC-UA、CAN 等。")])]),n._v(" "),t("li",[t("p",[n._v("工业微服务（Industrial Microservice）\n工业微服务是工业互联网平台中知识沉淀和复用的载体。微服务是以单一功能组\n件为基础，通过模块化组合方式实现“松耦合”应用开发的软件架构。工业微服\n务实现机理模型算法的集成，支撑工业互联网平台中的工业 APP 开发运行。工业\n微服务本质是经验知识的软件化和工具化，借助专业化的工具打造通用化的平台。")])]),n._v(" "),t("li",[t("p",[n._v("工业以太网（Industrial Ethernet）\n工业以太网是在工业环境的自动化控制及过程控制中应用以太网的相关组件及\n技术。工业以太网会采用 TCP/IP 协议，和 IEEE 802.3 标准兼容，但在应用层会加\n入各自特有的协议。")])]),n._v(" "),t("li",[t("p",[n._v("工业智能（Industrial Intelligence）\n工业人工智能（Industrial Artificial Intelligence），是工业领域中由计算\n机实现的具有自感知、自学习、自执行、自决策、自适应等特征的应用。")])]),n._v(" "),t("li",[t("p",[n._v("供应链管理（Supply Chain Management, SCM）\n供应链管理是在满足一定的客户服务水平的条件下，为了使整个供应链系统成本\n达到最小而把供应商、制造商、仓库、配送中心和渠道商等有效地组织在一起来\n进行的产品制造、转运、分销及销售的管理方法。")])]),n._v(" "),t("li",[t("p",[n._v("供应链金融（Supply Chain Finance）\n供应链金融是指银行围绕供应链中的核心企业，管理上下游中小企业的资金流和\n物流，并把单个企业的不可控风险转变为供应链企业整体的可控风险，通过全方\n位多渠道获取企业的各类信息，将风险控制在最低的金融服务。")])]),n._v(" "),t("li",[t("p",[n._v("供应链协同（Supply Chain Collaboration）\n供应链协同指两个或两个以上的企业为了实现某种战略目的，通过公司协议或联\n合组织等方式而结成的一种网络式联合体。")])]),n._v(" "),t("li",[t("p",[n._v("故障预测(Fault Prediction)\n故障预测是指基于存储在大数据存储与分析平台中的数据，通过设备使用数据、\n工况数据、主机及配件性能数据、配件更换数据等设备与服务数据，进行设备故\n障、服务、配件需求的预测，为主动服务提供技术支撑，延长设备使用寿命，降\n低故障率。")])]),n._v(" "),t("li",[t("p",[n._v("关联分析(Association Analysis)\n关联分析又称关联挖掘，就是在交易数据、关系数据或其他信息载体中，查找存\n在于项目集合或对象集合之间的频繁模式、关联、相关性或因果结构。")])]),n._v(" "),t("li",[t("p",[n._v("关系型数据库(Relational Database)\n关系数据库是建立在关系模型基础上的数据库，借助于集合代数等数学概念和方\n法来处理数据库中的数据。关系模型由关系数据结构、关系操作集合、关系完整\n性约束三部分组成。")])]),n._v(" "),t("li",[t("p",[n._v("机器学习（Machine Learning）\n机器学习是一门人工智能的科学，主要研究对象是人工智能，特别是如何在经验\n学习中改善具体算法的性能。")])]),n._v(" "),t("li",[t("p",[n._v("基础设施即服务(Infrastructure as a Service，IaaS）\n基础设施即服务是指用户通过互联网可以从完善的计算机基础设施获得服务，主\n要提供了虚拟计算、存储、数据库等基础设施服务，通常分为三种用法：公有云、\n私有云和混合云。其中公有云通常指第三方提供商为多个用户提供的能够使用的\n云；私有云是为一个客户单独使用而构建的，因而提供对数据、安全性和服务质\n量的最有效控制；混合云是公有云和私有云两种服务方式的结合。")])]),n._v(" "),t("li",[t("p",[n._v("可编程逻辑控制器（Programmable Logic Controller, PLC）\n可编程逻辑控制器是专为工业生产设计的一种数字运算操作的电子装置，它采用\n一类可编程的存储器，用于其内部存储程序，执行逻辑运算，顺序控制，定时，\n计数与算术操作等面向用户的指令，并通过数字或模拟式输入/输出控制各种类\n型的机械或生产过程。是工业控制的核心部分。")])]),n._v(" "),t("li",[t("p",[n._v("客户关系管理系统(Customer Relationship Management, CRM)\n客户关系管理系统是指用计算机自动化分析销售、市场营销、客户服务以及应用\n等流程的软件系统。它的目标是通过提高客户的价值、满意度、赢利性和忠实度\n来缩减销售周期和销售成本、增加收入、寻找扩展业务所需的新的市场和渠道。")])]),n._v(" "),t("li",[t("p",[n._v("控制设备（Control Equipment）\n控制设备是工业生产过程中用于控制执行器以及采集传感器数据的装置，包括分\n布式控制系统（DCS）的现场控制单元、可编程逻辑控制器（PLC）以及远程终端\n单元（RTU）等进行生产过程控制的单元设备。")])]),n._v(" "),t("li",[t("p",[n._v("离散制造（Intermittent/Discrete Manufacturing）\n离散制造是指产品的生产过程通常被分解成很多加工任务来完成，每项任务仅要\n求企业的一小部分能力和资源。")])]),n._v(" "),t("li",[t("p",[n._v("面向工业过程自动化的工业无线网络(Wireless Networks for Industrial\nAutomation Process Automation, WIA-PA)\n面向工业过程自动化的工业无线网络是中国工业无线联盟针对过程自动化领域\n制定的标准，是基于 IEEE 802.15.4 标准的用于工业过程测量、监视与控制的无\n线网络系统。")])]),n._v(" "),t("li",[t("p",[n._v("内存数据库(Memory Database)\n内存数据库基于全部数据都在内存中重新设计的数据存储架构，与磁盘数据管理相比，内存数据库在数据缓存、快速算法、并行操作方面也进行了相应的改进，所以数据处理速度比传统数据库的数据处理速度要快很多，一般都在 10 倍以上。内存数据库的最大特点是其“主拷贝”或“工作版本”常驻内存，即活动事务只与实时内存数据库的内存拷贝打交道。")])]),n._v(" "),t("li",[t("p",[n._v("平台即服务(Platform as a Service, PaaS )\n平台即服务是将应用服务的运行和开发环境作为一种服务提供的商业模式。PaaS\n是位于 IaaS 和 SaaS 模型之间的一种云服务，它提供了应用程序的开发和运行环境，\n借助于 PaaS 服务，使用户无须过多的考虑底层硬件，并可以方便的使用很多在构\n建应用时的必要服务。PaaS 能将现有各种业务能力进行整合，具体可以归类为应\n用服务器、业务能力接入、业务引擎、业务开放平台，向下根据业务能力需要测\n算基础服务能力，通过 IaaS 提供的 API 调用硬件资源，向上提供业务调度中心服\n务，实时监控平台的各种资源，并将这些资源通过 API 开放给 SaaS 用户。")])]),n._v(" "),t("li",[t("p",[n._v("企 业 专 用 工 业 APP（Industrial Applications Dedicated to the\nEnterprise）\n企业专用工业 APP 是对仅适用于工业企业内部使用的工业知识和经验软件化后形\n成的工业 APP，是工业 APP 基于技术体系维度进行分类的一种工业 APP。")])]),n._v(" "),t("li",[t("p",[n._v("企业资源计划管理系统(Enterprise Resource Planning, ERP)\n企业资源计划管理系统是指建立在信息技术基础上，以系统化的管理思想，为企\n业决策层及员工提供决策运行手段的管理平台。")])]),n._v(" "),t("li",[t("p",[n._v("区块链(Blockchain)\n区块链一种由多方共同维护，使用密码学保证传输和访问安全，能够实现数据一\n致存储、难以篡改、防止抵赖的技术体系。")])]),n._v(" "),t("li",[t("p",[n._v("人工智能(Artificial Intelligence)\n人工智能是研究、开发用于模拟、延伸和扩展人的智能的理论、方法、技术及应\n用系统的一门新的技术科学。")])]),n._v(" "),t("li",[t("p",[n._v("柔性制造(Flexible Manufacturing)\n柔性制造可以表述为两个方面：一个方面是指生产能力的柔性反应能力，也就是\n机器设备的小批量生产能力；第二个方面，指的是供应链的敏捷和精准的反应能\n力。")])]),n._v(" "),t("li",[t("p",[n._v("软件定义网络(Software Defined Networking, SDN)\n软件定义网络是一种新型的网络架构，它将网络控制平面和转发平面分离，采用\n集中控制替代原有分布式控制，并通过开放和可编程接口实现软件定义。")])]),n._v(" "),t("li",[t("p",[n._v("射频识别(Radio Frequency Identification, RFID)\n射频识别是一种通信技术，可通过无线电讯号识别特定目标并读写相关数据，而\n无需识别系统与特定目标之间建立机械或光学接触。")])]),n._v(" "),t("li",[t("p",[n._v("身份鉴别（Entity Authentication）\n身份鉴别指鉴别一个实体拥有其声明的身份。")])]),n._v(" "),t("li",[t("p",[n._v("深度学习（Deep Learning）\n深度学习是机器学习的分支，是一种以人工神经网路为架构，对资料进行表征学\n习的算法。深度学习是机器学习中一种基于对数据进行表征学习的算法。观测值\n可以使用多种方式来表示，如每个像素强度值的向量，或者更抽象地表示成一系\n列边、特定形状的区域等。")])]),n._v(" "),t("li",[t("p",[n._v("时间序列数据库(Temporal Database)\n时间序列数据库主要用于指处理带时间标签（按照时间的顺序变化，即时间序列\n化）的数据，带时间标签的数据也称为时间序列数据。基于时间序列数据的特点，\n关系型数据库无法满足对时间序列数据的有效存储与处理，因此迫切需要一种专\n门针对时间序列数据来做优化的数据库系统，即时间序列数据库。")])]),n._v(" "),t("li",[t("p",[n._v("数据安全（Data Security）\n数据安全指以数据为中心的安全，保护数据的可用性、完整性和机密性。")])]),n._v(" "),t("li",[t("p",[n._v("数据采集与监控系统(Supervisory Control And Data Acquisition, SCADA)\n数据采集与监控系统是以计算机为基础的生产过程控制与调度自动化系统。它可\n以对现场的运行设备进行监视和控制。应用于电力、冶金、石油、化工、燃气、\n铁路等诸多领域。SCADA 是工业互联网数据的重要来源，肩负着数据采集、测量、\n各类信号报警、设备控制以及参数调节等功能。")])]),n._v(" "),t("li",[t("p",[n._v("数据服务（Data Service）\n数据服务指提供数据采集、数据传输、数据存储、数据处理（包括计算、分析、\n可视化等）、数据交换、数据销毁等数据各种生存形态演变的一种信息技术驱动\n的服务。")])]),n._v(" "),t("li",[t("p",[n._v("数据管理（Data Management）\n数据管理是利用计算机硬件和软件技术对数据进行有效的收集、存储、处理和应\n用的过程，其目的在于充分有效地发挥数据的作用。在工业互联网平台领域，数\n据管理主要指提供面向工业场景的对象存储、关系数据库、NoSQL 数据库等数据\n管理和存储的工具。")])]),n._v(" "),t("li",[t("p",[n._v("数据脱敏（Data Desensitization）\n数据脱敏指对敏感信息进行数据变形处理，实现对敏感隐私信息的保护。")])]),n._v(" "),t("li",[t("p",[n._v("数据挖掘（Data Mining）\n数据挖掘一般是指从大量的数据中通过算法搜索隐藏于其中信息的过程。数据挖\n掘通常与计算机科学有关，并通过统计、在线分析处理、情报检索、机器学习、\n专家系统（依靠过去的经验法则）和模式识别等诸多方法来实现上述目标。")])]),n._v(" "),t("li",[t("p",[n._v("数据血缘关系（Data Lineage）\n数据血缘关系是指数据在产生、处理、流转到消亡过程中，数据之间形成的一种\n类似于人类社会血缘关系的关系。")])]),n._v(" "),t("li",[t("p",[n._v("数据预处理(Data Preprocessing)\n数据预处理指对原始数据的过滤、清洗、聚合、质量优化（剔除坏数据等）和语\n义解析。")])]),n._v(" "),t("li",[t("p",[n._v("数据治理(Data Governance)\n数据治理指对数据资产管理活动行使权力和控制的活动集合（规划、监控和执行）。\n数据治理职能指导其他数据管理职能如何执行。数据治理制定正确的原则、政策、\n流程、操作规程，确保以正确的方式对数据和信息进行管理。")])]),n._v(" "),t("li",[t("p",[n._v("数据资产(Data Asset)\n数据资产是指由企业拥有或者控制的，能够为企业带来未来经济利益的，以物理\n或电子的方式记录的数据资源，如文件资料、电子数据等。在企业中，并非所有\n的数据都构成数据资产，数据资产是能够为企业产生价值的数据资源。")])]),n._v(" "),t("li",[t("p",[n._v("数据资产管理(Data Asset Management)\n数据资产管理指规划、控制和提供数据及信息资产的一组业务职能，包括开发、\n执行和监督有关数据的计划、政策、方案、项目、流程、方法和程序，从而控制、\n保护、交付和提高数据资产的价值。数据资产管理是需要充分融合业务、技术和管理，来确保数据资产保值增值。")])]),n._v(" "),t("li",[t("p",[n._v("态势感知（Situation Awareness）\n态势感知指认知一定时间和空间内的环境要素，理解其意义，并预测它们即将呈\n现的状态，以实现决策优势。")])]),n._v(" "),t("li",[t("p",[n._v("网络安全漏洞（Cybersecurity Vulnerability）\n网络安全漏洞是网络系统在需求、设计、实现、配置、运行等过程中，无意或有\n意产生的缺陷或薄弱点。这些缺陷或薄弱点以不同形式存在于网络系统的各个层\n次和环节之中，一旦被恶意主体所利用，就会对网络系统的安全造成损害，从而\n影响网络系统的正常运行。")])]),n._v(" "),t("li",[t("p",[n._v("网络边界（Network Boundary）\n网络边界是工业互联网的安全计算环境边界，以及安全计算环境与安全通信网络\n之间实现连接并实施安全策略的相关部件。")])]),n._v(" "),t("li",[t("p",[n._v("网络虚拟化(Network Virtualization)\n网络虚拟化是能够实现网络资源动态调配、动态管理的技术。")])]),n._v(" "),t("li",[t("p",[n._v("物联网(Internet of Things, IoT)\n物联网是通信网和互联网的拓展应用和网络延伸，它利用感知技术与智能装臵对\n物理世界进行感知识别，通过网络传输互联，进行计算、处理和知识挖掘，实现\n人与物、物与物信息交互和无缝链接，达到对物理世界实时控制、精确管理和科\n学决策目的。")])]),n._v(" "),t("li",[t("p",[n._v("信息模型(Information Model)\n信息模型是对数据的语义提供概念、关系、约束、操作的模型化表示，实现信息\n的可共享、可组织。")])]),n._v(" "),t("li",[t("p",[n._v("信息物理系统(Cyber-Physical Systems, CPS)\n信息物理系统是一个综合计算、网络和物理环境的多维复杂系统，通过 3C\n（Computer、Communication、Control）技术的有机融合与深度协作，实现大型\n工程系统的实时感知、动态控制和信息服务。CPS 实现计算、通信与物理系统的\n一体化设计，可使系统更加可靠、高效、实时协同，具有重要而广泛的应用前景。")])]),n._v(" "),t("li",[t("p",[n._v("业务流程管理系统(Business Process Management, BPM)\n业务流程管理系统是一种以规范化的构造端到端的卓越业务流程为中心，以持续的提高组织业务绩效为目的的系统化方法。")])]),n._v(" "),t("li",[t("p",[n._v("云计算（Cloud Computing）\n云计算通过互联网，“按使用量付费”的方式提供随需应变的计算资源（从应用\n到数据中心）。其部署方式包括公有云、私有云和混合云。云计算通常简称为\n“云”。")])]),n._v(" "),t("li",[t("p",[n._v("云制造（Cloud Manufacturing）\n云制造是一种基于泛在网络，借助新兴制造技术、新兴信息技术、智能科学技术\n及制造应用领域技术等 4 类技术深度融合的数字化、网络化、智能化技术工具，\n构成以用户为中心的制造资源、产品与能力的服务云（网），使用户通过智能终\n端及制造云服务平台便能随时随地按需获取制造资源、产品与能力服务, 对制造\n全系统、全生命周期活动（产业链）中的人、机、物、环境、信息进行自主智能\n地感知、互联、协同、学习、分析、认知、决策、控制与执行，促使制造全系统\n及全生命周期活动中的人/组织、技术/设备、管理、数据、材料、资金（六要素）\n及人流、技术流、管理流、数据流、物流、资金流（六流）集成优化；形成一种\n用户为中心的互联化（协同化）、服务化、个性化（定制化）、柔性化、社会化、\n智能化的制造新模式。")])]),n._v(" "),t("li",[t("p",[n._v("知识模型（Knowledge Model）\n知识模型是将知识进行形式化和结构化的抽象，主要解决知识的表示、组织与交\n互关系，知识的有序化以及知识处理模型。知识模型不是知识，是知识的抽象，\n以便于计算机理解与处理。")])]),n._v(" "),t("li",[t("p",[n._v("知识图谱（Knowledge Graph）\n知识图谱是描述真实世界中存在的各种实体、概念及其关系构成的语义网络，现\n在泛指各种大规模知识库。")])]),n._v(" "),t("li",[t("p",[n._v("智慧能源(Smart Energy)\n智慧能源就是充分开发人类的智力和能力，通过不断技术创新和制度变革，在能\n源开发利用、生产消费的全过程和各环节融汇人类独有的智慧，建立和完善符合\n生态文明和可持续发展要求的能源技术和能源制度体系，从而呈现出的一种全新\n能源形式。简而言之，智慧能源就是指拥有自组织、自检查、自平衡、自优化等\n人类大脑功能，满足系统、安全、清洁和经济要求的能源形式。")])]),n._v(" "),t("li",[t("p",[n._v("智慧水务（Smart Water ）\n智慧水务是以业务与数据双驱动为核心，充分利用新一代信息与工业互联网技术，\n深入挖掘和广泛运用水务信息资源，通过水信息采集、传输、存储、处理和服务，\n全面提升水管理的效率和效能，实现更全面的感知，更主动的服务，更整合的资\n源，更科学的决策，更自动的控制和更及时的应对。")])]),n._v(" "),t("li",[t("p",[n._v("智能服务（Smart Service）\n智能服务是指基于模型驱动的统一服务框架，面向系统运维人员、业务决策者、\n系统集成商、应用开发人员等多种角色，提供开发服务框架和部署运营服务框架。")])]),n._v(" "),t("li",[t("p",[n._v("智能工厂（Intelligent Factory)\n智能工厂是在数字化工厂的基础上，利用物联网、大数据、人工智能等新一代信\n息技术加强信息管理以及合理计划排程，同时集智能手段和智能系统等新兴技术\n于一体，构建高效、和服务，提高生产过程可控性、减少生产线人工干预，节能、\n绿色、环保、舒适的人性化工厂。")])]),n._v(" "),t("li",[t("p",[n._v("智能故障诊断(Intelligent Fault Diagnosis)\n智能故障诊断对设备运行数据进行实时采集与处理分析，根据已设定的规则进行\n非法操作报警、设备异常报警、偏离预定位置报警等实时报警，以及故障远程诊\n断、维护，并相应与智能服务平台一键智能派工服务集成。")])]),n._v(" "),t("li",[t("p",[n._v("智能制造（Intelligent Manufacturing）\n智能制造是基于新一代信息通信技术与先进制造技术深度融合，贯穿于设计、生\n产、管理、服务等制造活动的各个环节，具有自感知、自学习、自决策、自执行、\n自适应等功能的新型生产方式。")])]),n._v(" "),t("li",[t("p",[n._v("智能资产（Smart Asset）\n智能资产指通过融合网络、计算、存储等 ICT 能力，具有自主化和协作化能力的\n资产（物）。")])]),n._v(" "),t("li",[t("p",[n._v("资产态势（Asset Situation）\n资产态势是指利用工业互联网设备特征库，识别接入的各种类型的工业互联网资\n产的型号、版本、运行状态等，获取工业互联网资产总数，并按区域、类型、重\n要程度分布等进行统计分析，实时呈现工业互联网资产的拓扑结构和分布态势。")])]),n._v(" "),t("li",[t("p",[n._v("资源接入工业 APP（Industrial Applications for Resource Accessing）\n资源接入工业 APP 是对服务器、超性能计算、工业设备、工业产品等工业资源的\n服务能力进行软件化后形成的工业 APP，可独立使用或接入到工程模型工业 APP、\n场景模版工业 APP 中组配化使用，是工业 APP 基于应用模式维度进行分类的一种\n工业 APP。")])])])])}),[],!1,null,null,null);i.default=e.exports}}]);